import React from 'react'
import ReactDOM from 'react-dom'

const Header = (props) => {
  return (
    <h1>{props.course}</h1>
  )
}

const Footer = (props) => {
  return (
    <div>
  <p>Number of exercises {props.exercises1 + props.exercises2 + props.exercises3}</p>
    </div>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
    name: 'Fundamentals of React',
    exercises: 10
  },
  {
    name: 'Using props to pass data',
    exercises: 7
  },
  {
    name: 'State of a component',
    exercises: 14
  }
]

    const Content = () => {
      const Part = (props) => {
        return(
          <div>
            <p>{props.name} {props.exercises}</p>
          </div>
        )
      }
    return (

    <div>
      <Part name={part1.name} exercises={part1.exercises}></Part>
      <Part name={part2.name} exercises={part2.exercises}></Part>
      <Part name={part3.name} exercises={part3.exercises}></Part>
      </div>
  )
}

  return (
    <div>
      <Header course={course} />
      <Content />
   <Footer exercises1={part1.exercises} exercises2={part2.exercises} exercises3={part3.exercises} />
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
