import React from 'react'
import ReactDOM from 'react-dom'

const Header = (props) => {
  return (
    <h1>{props.course}</h1>
  )
}

const Footer = (props) => {
  return (
    <div>
  <p>Number of exercises {props.exercises}</p>
    </div>
  )
}

const App = () => {
  const course = {
    name: 'Half Stack application development',
    parts: [
    {
    name: 'Fundamentals of React',
    exercises: 10
  },
  {
    name: 'Using props to pass data',
    exercises: 7
  },
  {
    name: 'State of a component',
    exercises: 14
  }
]
  }

    const Content = () => {
      const Part = (props) => {
        return(
          <div>
            <p>{props.name} {props.exercises}</p>
          </div>
        )
      }
    return (

    <div>
      <Part name={course.parts[0].name} exercises={course.parts[0].exercises}></Part>
      <Part name={course.parts[1].name} exercises={course.parts[1].exercises}></Part>
      <Part name={course.parts[2].name} exercises={course.parts[2].exercises}></Part>
      </div>
  )
}

  return (
    <div>
      <Header course={course.name} />
      <Content />
   <Footer exercises={course.parts[0].exercises + course.parts[1].exercises + course.parts[2].exercises}/>
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
