const App = () => {
  const courses = [
    {
      name: 'Half Stack application development',
      id: 1,
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        },
        {
          name: 'Redux',
          exercises: 11,
          id: 4
        }
      ]
    }, 
    {
      name: 'Node.js',
      id: 2,
      parts: [
        {
          name: 'Routing',
          exercises: 3,
          id: 1
        },
        {
          name: 'Middlewares',
          exercises: 7,
          id: 2
        }
      ]
    }
  ]

  const Header = (props) => {
    return(
      <h1>{props.courseName}</h1>
    )
  }

  const Content = (props) =>
  {
    return(
      <div>
        {props.parts.map(part =>  <Part name={part.name} exercises={part.exercises}></Part>)}
      </div>
    )
  }

  const Part = (props) => {
    return(
      <p>{props.name} : {props.exercises}</p>
    )
  }

  const Total = (props) => {


    return(
      <p>Number of exercises: {props.parts.reduce((prevValue, currentValue) => prevValue + currentValue.exercises, 0)}</p>
    )
  }

  const Course = (props) =>{

    return(
      <div>
        {props.courses.map(course => <p><Header courseName={course.name}/>
              <Content parts={course.parts}></Content>
              </p>)}

      </div>
    )
  }

  return <Course courses={courses} />
}
export default App
