const App = () => {
  const course = {
    id: 1,
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10,
        id: 1
      },
      {
        name: 'Using props to pass data',
        exercises: 7,
        id: 2
      },
      {
        name: 'State of a component',
        exercises: 14,
        id: 3
      },
      {
        name: 'Redux',
        exercises: 11,
        id: 4
      }
    ]
  }

  const Header = (props) => {
    return(
      <h1>{props.courseName}</h1>
    )
  }

  const Content = (props) =>
  {
    return(
      <div>
        {props.parts.map(part =>  <Part name={part.name} exercises={part.exercises}></Part>)}
      </div>
    )
  }

  const Part = (props) => {
    return(
      <p>{props.name} : {props.exercises}</p>
    )
  }

  const Total = (props) => {
    function reducer(total, num) {
      total = total + num.exercises
      return num.exercises;
    } 
    console.log(props.parts.reduce(reducer))
    return(
      <p>Number of exercises: {props.parts.map(part => <p>{part.exercises}</p>)}</p>
    )
  }

  const Course = (props) =>{

    return(
      <div>
      <Header courseName={props.course.name}></Header>
      <Content parts={props.course.parts}></Content>
      <Total parts={props.course.parts}></Total>
      </div>
    )
  }

  return <Course course={course} />
}
export default App
